import React from 'react'

function TodoForm() {
  return (
    <form onSubmit={handleSubmit}>
      <input
        className='title'
        type='text'
        name='inputText'
        placeholder='what do you want to achieve...?'
        value={inputText}
        onChange={(event) => setInputText(event.target.value)}
      />
      <button className='submitBtn'>ADD TASK</button>
    </form>
  )
}

export default TodoForm
