import React, { useCallback, useEffect, useState } from 'react'
import './TodoItem.css'
import { debounce } from 'loadash.debounce'
function TodoItem(props) {
  const [title, setTitle] = useState([])
  const optionList = ['none', 'low', 'medium', 'high']
  const [priority, setPriority] = useState('none')
  function handlePriority(e) {
    setPriority(e.target.value)
  }

  const [dropDown, setDropDown] = useState(false)
  function handleClick() {
    setDropDown(!dropDown)
  }
  const changeHandler = (event) => {
    setTitle(event.target.value)
    props.onUpdateNote('notes', setTitle, props.todo.id)
  }
  const debouncedChangeHandler = useCallback(debounce(changeHandler, 2000), [])
  // useEffect(() => {
  //   const timer = setTimeout(() => {
  //     props.onUpdateNote('notes', setTitle, props.todo.id)
  //   }, 2000)
  //   return () => clearTimeout(timer)
  // }, [title])

  return (
    <div>
      <div className='addTodo'>
        <input
          type='checkbox'
          className='checkBox'
          checked={props.todo.checkbox}
          onChange={() =>
            props.onCheckboxUpdate(
              'checkbox',
              !props.todo.checkbox,
              props.todo.id
            )
          }></input>
        <input
          type='text'
          className='todoTitle'
          value={props.todo.title}
          onClick={handleClick}
          onChange={debouncedChangeHandler}></input>
      </div>
      <div className='dropdown'>
        {dropDown && (
          <div>
            <textarea
              className='textNote'
              value={props.todo.notes}
              onChange={(event) => setTitle(event.target.value)}></textarea>
            <div className='elements'>
              <div>
                <label for='dateInput'>DueDate:</label>
                <input
                  type='date'
                  id='dueDate'
                  value={props.todo.dueDate}
                  onChange={(event) =>
                    props.onUpdateDueDate(
                      'dueDate',
                      event.target.value,
                      props.todo.id
                    )
                  }></input>
              </div>
              <div>
                <label for='primacy'>Priority:</label>
                <select
                  value={priority}
                  onClick={handlePriority}
                  onChange={(event) =>
                    props.onUpdatePriority(
                      'priority',
                      event.target.value,
                      props.todo.id
                    )
                  }>
                  {optionList.map((item) => {
                    return <option>{item}</option>
                  })}
                </select>
              </div>
              <button
                className='delTodo'
                onClick={() => props.onDelete(props.todo.id)}>
                Delete
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}
export default TodoItem
