import React, { useState } from 'react'
import './TodoItem.css'
function TodoItem(props) {
  const [title, setTitle] = useState(props.todo.title)
  const [notes, setNotes] = useState(props.todo.notes)
  const optionList = ['none', 'low', 'medium', 'high']
  const [priority, setPriority] = useState('none')

  function handlePriority(e) {
    setPriority(e.target.value)
  }

  const [dropDown, setDropDown] = useState(false)
  function handleClick() {
    setDropDown(!dropDown)
  }
  const handleChangeTitle = (e) => {
    setTitle(e.target.value)
    props.onUpdateTitle('title', e.target.value, props.todo.id)
  }
  const handleChangeNotes = (e) => {
    setNotes(e.target.value)
    props.onUpdateTitle('notes', e.target.value, props.todo.id)
  }

  return (
    <div>
      <div className='addTodo'>
        <input
          type='checkbox'
          className='checkBox'
          checked={props.todo.checkbox}
          onChange={() =>
            props.onCheckboxUpdate(
              'checkbox',
              !props.todo.checkbox,
              props.todo.id
            )
          }></input>
        <input
          type='text'
          className='todoTitle'
          value={title}
          onClick={handleClick}
          onChange={handleChangeTitle}></input>
      </div>
      <div className='dropdown'>
        {dropDown && (
          <div>
            <textarea
              className='textNote'
              value={notes}
              onChange={handleChangeNotes}></textarea>
            <div className='elements'>
              <div>
                <label for='dateInput'>DueDate:</label>
                <input
                  type='date'
                  id='dueDate'
                  value={props.todo.dueDate}
                  onChange={(event) =>
                    props.onUpdateDueDate(
                      'dueDate',
                      event.target.value,
                      props.todo.id
                    )
                  }></input>
              </div>
              <div>
                <label for='primacy'>Priority:</label>
                <select
                  value={priority}
                  onClick={handlePriority}
                  onChange={(event) =>
                    props.onUpdatePriority(
                      'priority',
                      event.target.value,
                      props.todo.id
                    )
                  }>
                  {optionList.map((item) => {
                    return <option>{item}</option>
                  })}
                </select>
              </div>
              <button
                className='delTodo'
                onClick={() => props.onDelete(props.todo.id)}>
                Delete
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}
export default TodoItem
