import React, { useState } from 'react'
import './TodoItem.css'
function TodoItem(props) {
  // const [checkbox, setCheckBox] = useState(false)
  // function updateCheckBox() {
  //     let tempTodos = JSON.parse(localStorage.getItem('items'))

  //     localStorage.setItem('items', JSON.stringify(tempTodos))
  // }

  function deleteTodo(id) {
    const removeTodo = items.filter((todo) => {
      return todo.id !== id
    })
    setTodos(removeTodo)
  }

  const optionList = ['none', 'low', 'medium', 'high']
  const [priority, setPriority] = useState('none')
  function handlePriority(e) {
    setPriority(e.target.value)
  }

  const [dropDown, setDropDown] = useState(false)
  function handleClick() {
    setDropDown(!dropDown)
  }

  return (
    <div>
      <div className='addTodo'>
        <input
          type='checkbox'
          className='checkBox'
          value={props.todo.checkbox}
        ></input>
        <input
          type='text'
          className='todoTitle'
          value={props.todo.title}
          onClick={handleClick}
        ></input>
      </div>
      <div className='dropdown'>
        {dropDown && (
          <div>
            <textarea className='textNote' value={props.todo.note}></textarea>
            <div className='elements'>
              <div>
                <label for='dateInput'>DueDate:</label>
                <input
                  type='date'
                  id='dueDate'
                  value={props.todo.dueDate}
                ></input>
              </div>
              <div>
                <label for='primacy'>Priority:</label>
                <select value={priority} onChange={handlePriority}>
                  {optionList.map((item) => {
                    return <option>{item}</option>
                  })}
                </select>
              </div>
              <button className='delTodo' onClick={deleteTodo(props.todo.id)}>
                Delete
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}
export default TodoItem
